import React, { useEffect, useState } from 'react';
import './customer.css';
import Sidebar from '../Sidebar/sidebar';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import Pagination from '../Pagination/paagination';
import { toast, ToastContainer } from 'react-toastify';

interface Customer {
  Id: number;
  fullName: string;
  address: string;
  contactDetail: number;
}



function ManageCustomer() {
  const navigate = useNavigate();
  const BASE_URL = process.env.REACT_APP_API_BASE_URL;

  const [orders, setOrders] = useState<Customer[]>([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [currentPage, setCurrentPage] = useState(1);
  const [itemsPerPage] = useState(4);



  useEffect(() => {
    const fetchOrders = async () => {
      try {
        const response = await axios.get(`${BASE_URL}customer`);
        setOrders(response.data);
      } catch (err) {
        setError('Error fetching orders');
      } finally {
        setLoading(false);
      }
    };

    fetchOrders();
  }, []);




  const handleDeleteClick = async (id: number) => {
    try {
      const response = await axios.delete(`${BASE_URL}customer/${id}`);
      if (response.status === 200) {
        setOrders(orders.filter(order => order.Id !== id));
        toast.success(`Customer delete successfully`)
      }
    } catch (error) {
      console.error('Error deleting order:', error);
    }
  };

  const handleEditClick = (id: number) => {
    navigate('/addCustomer', { state: { id } });
  };
  
  const handleAddCustomerClick = () => {
    navigate('/addCustomer');
  };
  

  const indexOfLastItem = currentPage * itemsPerPage;
  const indexOfFirstItem = indexOfLastItem - itemsPerPage;
  const currentItems = orders.slice(indexOfFirstItem, indexOfLastItem);

  const paginate = (pageNumber: number) => setCurrentPage(pageNumber);


  return (
    <>
      <Sidebar />
      <ToastContainer position="top-right" autoClose={3000} />

      <div className={`container-fluid `}>
        <div className="row">
          <div className="col-md-2">

          </div>
          <div className="col-md-10">
            <span >
              <h1 className='pt-2' >Manage Customer</h1>
            </span>
            <div className="d-flex justify-content-end mt-2">

              <button className="btn btn-primary " onClick={handleAddCustomerClick}
              >Add Customer</button>
            </div>
            <div className="d-flex justify-content-center mt-1">


              <table className="table table-bordered">
                <thead>
                  <tr>
                    <th scope="col">Id</th>
                    <th scope="col">Customer Name</th>
                    <th scope="col">Address</th>
                    <th scope="col">Contact Detail</th>
                    <th scope="col">Action</th>
                  </tr>
                </thead>
                <tbody>
                  {currentItems.map((order) => (
                    <tr key={order.Id}>
                      <th scope="row">{order.Id}</th>
                      <td>{order.fullName}</td>
                      <td>{order.address}</td>
                      <td>{order.contactDetail}</td>
                      <td>
                        <button className="btn ">
                          <i className="bi bi-pencil-square" onClick={() => handleEditClick(order.Id)}></i>
                        </button>
                        <button className="btn"  >
                          <i className="bi bi-trash" onClick={() => handleDeleteClick(order.Id)}></i>
                        </button>
                      </td>
                    </tr>
                  ))}
                </tbody>
              </table>
              <span className=' d-flex  fixedCustomerPagination'>
                <Pagination
                  itemsPerPage={itemsPerPage}
                  totalItems={orders.length}
                  paginate={paginate}
                  currentPage={currentPage}
                />
              </span>

            </div>
          </div>
        </div>
      </div>
    </>
  );
}

export default ManageCustomer;
