"use client";

import React from 'react';
import "./sidebar.css";
import 'bootstrap/dist/css/bootstrap.min.css';
import { useNavigate } from 'react-router-dom';
import LOGO from '../../assets/images/4.png'; // Adjust the path according to your folder structure



function Sidebar() {
  const navigate = useNavigate();

  const handleLogout = () => {
    localStorage.removeItem("authToken");
    window.location.href = "/";
  };

  return (
    <div className="sidebar1 d-flex flex-column vh-100">
      <img src={LOGO} alt="Description of the image" style={{ width: '90px', height: 'auto' }} />
      {/* <h3>BAYBUY</h3> */}

      <ul className="flex-grow-1">
        <li onClick={() => navigate('/manageOrder')} >Manage Order</li>
        <li onClick={() => navigate('/manageCustomer')} >Manage Customer</li>
        <li onClick={() => navigate('/about')} >About us</li>
      </ul>
      
      <div className="d-flex  align-items-end">
        <button className="btn  p-0 text-decoration-none logoutbtn" onClick={handleLogout}>
          Logout
        </button>
      </div>
    </div>
  );
}

export default Sidebar;
